mapper.select:

SELECT NVL(MAX('Y'), 'N') AS validYn
  FROM sdm_LoginSession
 WHERE sessionKey = #{sessionKey}


SELECT gwID, panID, snID
	 , globalID, manufacturer, productNo, location
	 , longitude, latitude, altitude, role, roleList
	 , parentNodeIDList, supportedOperationList, supportedAttributeList
	 , tdIDList, monitoringMode, monitoringPeriod
  FROM sdm_Node
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}


SELECT gwID, panID, snID, tdID
	 , manufacturer, productNo, transducerCategory, transducerType
	 , unit, dataType, rangeMin, rangeMax, rangeOffset, level
	 , supportedOperationList, supportedAttributeList
  FROM sdm_Transducer
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}


SELECT gwID, panID, snID, tdID
	 , manufacturer, productNo, transducerCategory, transducerType
	 , unit, dataType, rangeMin, rangeMax, rangeOffset, level
	 , supportedOperationList, supportedAttributeList
  FROM sdm_Transducer
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}
   AND tdID  = #{tdID}


SELECT gwID || ':' || panID || ':0:0' AS address
	 , statusCode
  FROM sdm_Pan
 WHERE gwID  = #{gwID}
   AND panID = #{panID}


SELECT gwID || ':' || panID || ':0:0' AS address
	 , statusCode
  FROM sdm_Pan
 WHERE gwID  = #{gwID}
   AND panID = #{panID}

 UNION ALL

SELECT gwID || ':' || panID || ':' || snID || ':0' AS address
	 , statusCode
  FROM sdm_Node
 WHERE gwID  = #{gwID}
   AND panID = #{panID}

 UNION ALL

SELECT gwID || ':' || panID || ':' || snID || ':' || tdID AS address
	 , statusCode
  FROM sdm_Transducer
 WHERE gwID  = #{gwID}
   AND panID = #{panID}


SELECT gwID || ':' || panID || ':' || snID || ':0' AS address
	 , statusCode
  FROM sdm_Node
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}


SELECT gwID || ':' || panID || ':' || snID || ':0' AS address
	 , statusCode
  FROM sdm_Node
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}

 UNION ALL

SELECT gwID || ':' || panID || ':' || snID || ':' || tdID AS address
	 , statusCode
  FROM sdm_Transducer
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}


SELECT gwID || ':' || panID || ':' || snID || ':' || tdID AS address
	 , statusCode
  FROM sdm_Transducer
 WHERE gwID  = #{gwID}
   AND panID = #{panID}
   AND snID  = #{snID}
   AND tdID  = #{tdID}

mapper.insert:

INSERT INTO sdm_AssessValue (
			category
		  , type
		  , date
		  , value
		  , longitude
		  , latitude
		  , altitude
		  , position
		  )
	VALUES (
		    #{category}
		  , #{type}
		  , #{date}
		  , #{value}
		  , #{longitude}
		  , #{latitude}
		  , #{altitude}
		  , ST_POINTFROMTEXT('POINT(${longitude} ${latitude})',0)
		  )
